var values = flow.get("values") || {}, 
    timestamps = flow.get("timestamps") || {},  
    oldValue = values[msg.topic],
    oldTimestamp = timestamps[msg.topic],
    skip;

msg.old = {
    timestamp: oldTimestamp,
    value: oldValue
}

skip = 
    oldValue !== undefined &&
    (Math.abs(msg.payload - oldValue) < 0.5) &&
    oldTimestamp !== undefined &&
    (Math.abs(Date.now() - oldTimestamp) < 60 * 1e3);

if (skip)
    return null;

/*
node.warn("-- " + msg.topic + " --\n" + 
    "val: "+msg.payload +"\n"+ 
    "old: "+oldValue +"\n"+ 
    "tsp: "+(Date.now())+"\n"+
    "old: "+oldTimestamp+"\n"+
    (skip? "true":"false")
);
*/

values[msg.topic] = msg.payload;
flow.set("values", values);

timestamps[msg.topic] = Date.now();
flow.set("timestamps", timestamps);

return msg;

// check if numeric value
var isNumeric = !isNaN(parseFloat(msg.payload)) && isFinite(msg.payload);

if (!isNumeric) {
    return null;
}

// prepare json array
msg.payload = JSON.stringify([
    [Date.now(), msg.payload]
]);

return msg;

if (msg.statusCode == 200)
    return [null, null];

var error = {
    payload: "HTTP " + msg.statusCode + " " + msg.payload,
};

return [msg, error];

// topic
var topic = msg.topic.split("/");
msg.topic = topic[topic.length-1].replace('.', '');

// payload
if (msg.payload == "off")
    msg.payload = 0;
else if (msg.payload == "on")
    msg.payload = 100;

return msg;

// -- SetMode --
if (msg.topic == "SetMode") {
    var setmode = msg.payload.split(';');
    return {
        topic: "EbusFlowTempDesired",
        payload: 0+setmode[1]
    }
}

// -- HcStarts --

if (msg.topic == "HcStarts") {
    flow.set("HcStarts", +msg.payload);
    return null;
}

if (msg.topic == "HcUnderHundredStarts") {
    var hcstarts = +flow.get("HcStarts");
    if (!hcstarts)
        return null;
    var totalStarts = hcstarts + parseInt(msg.payload)
    return {
        "topic": "HcStarts",
        "payload": totalStarts
    };
}

// -- Status01 --

if (msg.topic !== "Status01")
    return msg;

var registers = ['HeatingFlowTemp', 'HeatingReturnTemp', 'OutsideTemp', null, null, 'VentState'];
var values = msg.payload.split(';');
var result = [];

registers.forEach(function(reg, i) {
    if (!reg)
        return;

    var value = values[i];

    if (reg == "VentState") {
        var hcvent = 0, hwcvent = 0;
        switch (value) {
            case "on":
                hcvent = 100;
                break;
            case "4":
                hwcvent = 100;
                break;
        }
        result.push({
            topic: "HcVent",
            payload: hcvent
        }, {
            topic: "HwcVent",
            payload: hwcvent
        });
        return;
    }

    result.push({
        topic: reg,
        payload: value
    });
});

return [result];

var hcStarts = parseInt(global.get("HcStarts"));

if (hcStarts) {
    msg.payload = hcStarts + parseInt(msg.payload);
    msg.topic = "HcStarts";
}

return msg;

var uuid = msg.uuid;
var value = +msg.payload;
var time = Date.now();

msg.headers = {
    "Content-type" : "application/json"
}

var json = {
    data: [{
        uuid: uuid,
        tuples: [
            [time, value]
        ]
    }]
}

msg.payload = JSON.stringify(json);

return msg;

var res = [],
    sdm = 'SDM630(full).';

// Leistungen

var bezug = 0, einspeisung = 0, erzeugung = 0;

for (var i=1; i<=3; i++) {
    res.push({
        topic: sdm + "Meter1.L" + i,
        payload: msg.payload[0].Power["L"+i]
    });
    if (i <= 2) res.push({
        topic: sdm + "Meter2.L" + i,
        payload: msg.payload[1].Power["L"+i]
    });

    var value = msg.payload[0].Power["L"+i];
    if (value > 0)
        bezug += value;
    else
        einspeisung -= value;
    erzeugung -= msg.payload[1].Power["L"+i];
}

res.push({
    topic: sdm + "Erzeugung",
    payload: erzeugung
});
res.push({
    topic: sdm + "Bezug",
    payload: bezug
});
res.push({
    topic: sdm + "Einspeisung",
    payload: einspeisung
});

return [res];

if (!msg.topic)
    return msg;

var values = flow.get("values") || {}, 
    timestamps = flow.get("timestamps") || {},  
    oldValue = values[msg.topic],
    oldTimestamp = timestamps[msg.topic],
    skip;

// at least 1 minute, even if value change
if (
    oldTimestamp !== undefined && 
    (Math.abs(Date.now() - oldTimestamp) < 1*60 * 1e3) &&
    msg.skip !== false
)
    return null;

if (
    (oldValue !== undefined && (Math.abs(msg.payload - oldValue) < 0.001)) &&
    (oldTimestamp !== undefined && (Math.abs(Date.now() - oldTimestamp) < 10*60 * 1e3)) &&
    msg.skip !== false
) {
    return null;
}

values[msg.topic] = msg.payload;
flow.set("values", values);

timestamps[msg.topic] = Date.now();
flow.set("timestamps", timestamps);

return msg;

if (msg.statusCode == 200)
    return [null, null];

var error = {
    payload: "HTTP " + msg.statusCode + " " + msg.payload,
};

return [msg, error];

var registers = {
'700': [
	'SystemFlowTemp', // Status01
	'Hc1ActualFlowTempDesired',
	'Hc1FlowTemp',
	'Hc1MixerMovement',
	'Hc1PumpStatus',
	'Hc2ActualFlowTempDesired',
	'Hc2FlowTemp',
	'Hc2MixerMovement',
	'Hc2PumpStatus',
	'HwcStorageTemp',
	'HwcTempDesired'
],
'bai': [
	'CirPump',
	'FlowTempDesired',
	'HcHours',
	'HwcHours',
	'ModulationTempDesired',
	// 'Statenumber',
	// 'TargetFanSpeed',
	// 'TargetFanSpeedOutput',
	'PrimaryCircuitFlowrate',
	//'FlowsetHcMax',
	'ReturnRegulation',
	'ExtFlowTempDesiredMin',
	"PumpPower",
	"PumpPowerDesired"
],
'vr_70': [
    'SensorData'
]};

var result = [];

for (var circuit in registers) {
	registers[circuit].forEach(function(reg) {
		result.push({
		    topic: "ebusd/"+circuit+"/"+reg+"/get",
		    payload: 1
		});
	});
}

return [result];

// allow only Mixer messages below 100

if (msg.topic.match(/Hc.MixerMovement/)) {
    if (Math.abs(+msg.payload) < 50) {
        var date = new Date();
        var hour = date.getHours();
        var min = date.getMinutes();
        var sec = date.getSeconds();

        hour = (hour < 10 ? "0" : "") + hour;
        min = (min < 10 ? "0" : "") + min;
        sec = (sec < 10 ? "0" : "") + sec;

        return {
            topic: "[alert] Heating mixer",
            payload: 
                msg.topic + " stuck or dead.\n"+
                "Last observed: " + hour +":"+ min +":"+ sec
        };
    }
}

return null;

var date = new Date();
var hour = date.getHours();
var min = date.getMinutes();
var sec = date.getSeconds();

hour = (hour < 10 ? "0" : "") + hour;
min = (min < 10 ? "0" : "") + min;
sec = (sec < 10 ? "0" : "") + sec;

return {
    topic: "[alert] ebusd",
    payload: 
        "No message received for 5min. Ebusd or MQTT dead.\n"+
        "Last observed: " + hour +":"+ min +":"+ sec
};

var registers = [
    "HcHours", "HcStarts", "HcUnderHundredStarts",
    "PrEnergySumHc1", "PrEnergySumHwc1"
], result = [];

registers.forEach(function(reg) {
	result.push({
	    topic: "ebusd/bai/"+reg+"/get",
	    payload: 1
	});
});


return [result];

// S1 = SystemFlowTemp
// S5 = Hc1FlowTemp
// S6 = Hc2FlowTemp

var registers = ['S1', null, null, null, 'S5', 'S6'];
var values = msg.payload.split(';');
var result = [];

registers.forEach(function(reg, i) {
    if (!reg)
        return;

    result.push({
        topic: reg,
        payload: values[i]
    });
});

return [result];

var readings = global.get("sdm") || {};

var m, re = new RegExp('^sdm630/(.+?)/(.+)$');
if ((m = re.exec(msg.topic)) !== null) {
    var meter = m[1], reg = m[2];

    readings[meter] = readings[meter] || {};
    readings[meter][reg] = parseFloat(msg.payload);

    msg.payload = readings;
    global.set("sdm", readings);
}

return msg;

return null;

// cache
if (["Hc1ActualFlowTempDesired", "Hc2ActualFlowTempDesired", "SetMode"].indexOf(msg.topic) >= 0) {
    // remember values
    flow.set(msg.topic, msg.payload);

    // log
    node.send({
        "topic": "nodered/setmode/" + msg.topic,
        "payload": msg.payload
    })
}

// exit unless SetMode
if (msg.topic !== "SetMode") {
    return null;
}

var setmode = msg.payload.split(";");

if (setmode[0] !== "auto" || setmode[4] !== "0" || setmode[6] !== "0")
    return null;

var hcMax = Math.max(
    0+flow.get("Hc1ActualFlowTempDesired"),
    0+flow.get("Hc2ActualFlowTempDesired")
);

// log
node.send({
    "topic": "nodered/setmode/hcmax",
    "payload": hcMax
});

if (hcMax <= 0)
    return null;

if (setmode[0] == "auto")
    setmode[0] = "0";

if (0+setmode[1] <= hcMax)
    return null;

setmode[1] = (hcMax + 1.0).toFixed(1);
mode = setmode.join(";");

// log
node.send({
    "topic": "nodered/setmode/mode",
    "payload": mode
});

// mqtt
msg = {
    "topic": "ebusd/bai/SetModeOverride/set",
    "payload": mode
}
/*
// ebusctl
msg = {
    "topic": "exec",
    "payload": "-s nas.fritz.box w -c bai -s 10 SetModeOverride '" + mode + "'"
}
*/
return msg;
